    @IBOutlet private weak var animalImageView: UIImageView!
    @IBOutlet private weak var titleLabel: UILabel!
    @IBOutlet private weak var imageContentLabel: UILabel!
    
    override func viewDidLoad() {
        super.viewDidLoad()
        updateUI()
    }
    
    private func updateUI() {
        animalImageView.image = UIImage(named: animalNames[index])
        titleLabel.text = "\(index + 1)/ \(animalNames.count)"
        imageContentLabel.text = imageContent[index]
    }
    
    @IBAction func nextButton(_ sender: UIButton) {
        if index < animalNames.count - 1 {
            index += 1
        }
    }
    @IBAction func backButton(_ sender: UIButton) {
        if index > 0 {
            index -= 1
        }
    }
